				<div className={h.container} style={{ cursor: 'pointer', overflow: 'scroll' }}>
					<div className={s.mycard} >
						{postLoading ? (
							postCard.results.map((info, index) => {
								return (
									<Card
										{...info}
										addBasket={addBasket}
										isAddedToCart={karzinkaTovar.some((item) => item.id === info.id)}
										key={index}
										isActive={isActive}
									/>
								);
							})
						) : (
							Goods.map((info, index) => {
								return (
									<Card
										{...info}
										addBasket={addBasket}
										isAddedToCart={karzinkaTovar.some((item) => item.id === info.id)}
										key={index}
										isActive={isActive}
									/>
								);
							})
						)}
					</div>
				</div>
